INSERT INTO SIAT_CT.HISTORY VALUES (SIAT_CT.HISTORY_SEQ.NEXTVAL,'UPDATE',CURRENT_TIMESTAMP,(SELECT U.ID FROM SIAT_CT.USERS U WHERE U.LOGIN = 'ALIYOU'),NULL,NULL,'User','ROOT','User [id=' || (SELECT U.ID FROM SIAT_CT.USERS U WHERE U.LOGIN = 'ALIYOU') || '], add new transfers = '||(SELECT WM_CONCAT(T.ID) FROM SIAT_CT.TRANSFER T WHERE T.ASSIGNED_USER_ID = (SELECT U.ID FROM SIAT_CT.USERS U WHERE U.LOGIN = 'ALIHOU' AND U.DELETED = 0))||' from '||(SELECT U.ID FROM SIAT_CT.USERS U WHERE U.LOGIN = 'ALIHOU' AND U.DELETED = 0));
UPDATE SIAT_CT.TRANSFER T SET T.ASSIGNED_USER_ID = (SELECT U.ID FROM SIAT_CT.USERS U WHERE U.LOGIN = 'ALIYOU') WHERE T.ASSIGNED_USER_ID = (SELECT U.ID FROM SIAT_CT.USERS U WHERE U.LOGIN = 'ALIHOU' AND U.DELETED = 0);

