INSERT INTO SIAT_CT.HISTORY VALUES (SIAT_CT.HISTORY_SEQ.NEXTVAL,'UPDATE',CURRENT_TIMESTAMP,(SELECT U.ID FROM SIAT_CT.USERS U WHERE U.LOGIN = 'ALIHOU' AND U.DELETED = 0),NULL,NULL,'User','ROOT','User [id=' || (SELECT U.ID FROM SIAT_CT.USERS U WHERE U.LOGIN = 'ALIHOU' AND U.DELETED = 0) || ', deleted=1]');
UPDATE SIAT_CT.USERS U SET U.DELETED = 1 WHERE U.LOGIN = (SELECT U.LOGIN FROM SIAT_CT.USERS U WHERE U.LOGIN = 'ALIHOU' AND U.DELETED = 0) AND U.DELETED = 0;
